showRule:
@js:

//版本号:1.0.1

let key = String(java.hexDecodeToString(result));

let url = `https://hanyuapp.baidu.com/dictapp/${key.length == 1 ? ('word/detail_getworddetail?wd=' + key) : ('swan/termdetail?wd=' + key)}&client=pc&lesson_from=xiaodu&${key.length == 1 ? ('smp_names=wordNewData1') : ('source_tag=2')}`;

try {

    result = java.ajax(url);

    var aly = new JavaImporter(Packages.com.jayway.jsonpath);

    with (aly) {

        var rr = JsonPath.using(

            Configuration.builder()

                .options(Option.SUPPRESS_EXCEPTIONS).build()

        ).parse(result);

    }

    let re = '';


    let iV = rr.read('$.data.idiomVersion');

    if (iV) {

        //成语

        let py = rr.read('$.data.pinyin');

        let gs = rr.read('$.data.story[*]');

        let df1 = rr.read('$.data.definitionInfo.definition');

        let bb = rr.read('$.data.baobian');

        let dm = rr.read('$.data.definitionInfo.detailMeans[*]');

        dm = Array.from(dm).map(m => `${m.word}：${m.definition}`).join('<br>');

        let cc = rr.read('$.data.chuChu[*]');

        cc = Array.from(cc).map(m => `${m.citeOriginalText}--${m.dynasty!=''?(m.dynasty+'●'):''}${m.author!=''?(m.author+'●'):''}${m.source!=''?(m.source):''}${m.sourceChapter!=''?('-'+m.sourceChapter):''}`).join('<br>');

        if (gs!='[]') gs = `<br><h3>成语故事</h3><br><p>${Array.from(gs).join('<br>')}</p>`;

        else gs = '';

        if (cc!='') cc = `<br><h3>出处</h3><br><p>${cc}</p>`;


        re += `<br><h3>${py}</h3><br><h3>基本释义 ${bb!=''?('['+bb+']'):''}</h3><br><p>${df1}</p><br><p>${dm}</p>${gs}${cc}`;



     }

    else {

        //   let nm = rr.read('$.data..name');

        let py = rr.read('$.data..comprehensiveDefinition[*].pinyin');

        for (let i = 0; i < py.length; i++) {

            re += `<br><h3>${py[i]}</h3>`;

            let cx1 = rr.read('$.data..comprehensiveDefinition[' + i + '].basicDefinition[*]cixing');

            let df1 = rr.read('$.data..comprehensiveDefinition[' + i + '].basicDefinition[*]definition');

            for (let i = 0; i < df1.length; i++) {

                if (!i) re += `<br><h3>基本释义</h3>`;

                let ccx = Array.prototype.join.call(cx1[i], ',');

                re += `<br><p>${(i + 1) + '、' + (ccx == '' ? '' : '[' + ccx + '] ') + df1[i]}</p>`;

            }

            let cx2 = rr.read('$.data..comprehensiveDefinition[' + i + '].detailDefinition[*]cixing');

            let df2 = rr.read('$.data..comprehensiveDefinition[' + i + '].detailDefinition[*]definition');

            for (let i = 0; i < df2.length; i++) {

                if (!i) re += `<br><h3>详细释义</h3>`;

                let ccx = Array.prototype.join.call(cx2[i], ',');

                re += `<br><p>${(i + 1) + '、' + (ccx == '' ? '' : '[' + ccx + '] ') + df2[i]}</p>`;

            }

        }

    }

        let bd = '<br><br>百度汉语中没有这个词';

        let xd = rr.read('$.data.baikeInfo..baikeMean');

        if (xd != '[]') xd = `<br><b>${xd[0]}</b><br><br>`;

        else xd='';

        if (re != '') re += '<br><br>';

        if (re || xd) bd = re + xd;

        `<h1><a href="https://hanyu.baidu.com/hanyu-page/term/detail?wd=${key}">${key}</a></h1>${bd}`

    

} catch (e) { "出错啦：" + e }
