function html(xx,yy,zz) {
let lycht=String.raw`
<html>
<head>
<meta name="viewport" content="width=device-width, initial-scale=1.0, user-scalable=no">
<meta name="referrer" content="no-referrer">
    <title>${zz}</title>
    <style>
        body {
            display: flex;
            flex-direction: column;
            align-items: center;
            height: 100%;
            min-height: 80vh;
            margin: 0;
        }
        #tu1 {
        	width: 100vw;
        	margin: 0;}
        	#tu1>img {
        	width: 100%;
        	height: auto;}
        #tu2 {
        	width: 100vw;
        	overflow: hidden;
        	margin: 0;}
        #tu2>img {
        	width: 100%;
        	height: 100%;
        	object-fit: cover;
        	object-position: center 100%;}
    </style>
</head>
<body>
<div id="tu1">
<img src="${xx}" alt="日历未加载">
</div>
<div id="tu2">
<img ${/^[0n]/.test(yy)?'hidden':''} src="${yy}" alt="天气未加载">
</div>
<script>
document.addEventListener('DOMContentLoaded', () => {
  document.querySelectorAll('img').forEach(img => {
  	let url=img.src;
  	if (/^http/i.test(url)) {
    img.addEventListener('error', function() {
      retryImageLoad(this,5);
    });
    img.dataset.originalSrc = url;
    }
  });
});
function retryImageLoad(img, maxRetries = 3) {
    let retryCount = parseInt(img.dataset.retryCount) || 0;
    if (retryCount >= maxRetries) {
      return;
    }
    img.dataset.retryCount = ++retryCount;
    const newSrc = img.dataset.originalSrc + 
                  '?retry=' + Date.now();
    // 延迟重试
    setTimeout(() => {
      img.src = newSrc;
      img.alt = img.alt+'，重试'+retryCount+'次';
    }, 3**retryCount*30);
  }
</script>
</body>
</html>`;
return `data:text/html;base64,${this.java.base64Encode(lycht)}`;
}

